(()=>{"use strict";document.getElementById("site-nav-toggle-button").addEventListener("click",(()=>{document.getElementById("site-nav").classList.toggle("show"),document.getElementById("site-nav").classList.contains("show")?(document.getElementById("site-nav-toggle-button").classList.add("close"),document.body.classList.add("on-site-nav-open")):(document.getElementById("site-nav-toggle-button").classList.remove("close"),document.body.classList.remove("on-site-nav-open"))}))})();